import java.util.*;

class State
{
    int m;
    int n;
    String s;
    String t;
    
    public State(int m, int n)
    {
        this.m = m;
        this.n = n;
    }
    
    @Override
    public boolean equals(Object obj)
    {
        State s = (State)obj;
        
        if(s!=null && s instanceof State)
        {
            if(s.m==this.m && s.n==this.n)
            {
                return true;
            }
        }
        
        return false;
    }
    
    @Override
    public int hashCode()
    {
        int hash = 7*this.m + 31*this.n;
        
        return hash;
    }
}

public class DistinctSubSequence
{
	static HashMap<State,Integer> map = new HashMap<State,Integer>();

	public static void main(String[] args)
	{
		//String s = "";
		String s = "rabbbit";
		String t = "rabbit";;

		System.out.println(numDistinctMemo(s,t,0,0));

	}

	public static int numDistinctMemo(String s, String t, int m, int n)
    {
        if(s.equals(t))
        return 1;
        
        if(m==s.length() && n<t.length())
        return 0;
        
        if(n==t.length())
        return 1;
        
        State state = new State(m,n);
        
         
         
        if(map.containsKey(state))
        {
           System.out.println("state present");
            return map.get(state);
        }
        
        
        int count = 0;
        
        if(s.charAt(m) == t.charAt(n))
        {
            count = numDistinctMemo(s,t,m+1,n+1) + numDistinctMemo(s,t,m+1,n);
        }
        else
        count = numDistinctMemo(s,t,m+1,n);
        
        map.put(state,count);
        
        return count;
    }

}